import React, { useEffect, useState } from "react";
import axios from "axios";
import TodoItem from "../components/TodoItem";

export default function Home({ token, setToken }) {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [dueDate, setDueDate] = useState("");

  const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
    headers: { Authorization: `Bearer ${token}` },
  });

  const fetchTodos = async () => {
    try {
      const res = await api.get("/todos");
      setTodos(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (e) => {
    e.preventDefault();
    if (!task.trim()) return;
    try {
      await api.post("/todos", { text: task, dueDate });
      setTask("");
      setDueDate("");
      fetchTodos();
    } catch (err) {
      console.error("Error adding todo:", err);
    }
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem("token");
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #1d2671 0%, #c33764 100%)",
        fontFamily: "'Poppins', sans-serif",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "40px 20px",
        color: "#fff",
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "800px",
          background: "rgba(255,255,255,0.15)",
          borderRadius: "25px",
          padding: "40px 30px",
          boxShadow: "0 10px 30px rgba(0,0,0,0.3)",
          backdropFilter: "blur(12px)",
        }}
      >
        <h1 style={{ textAlign: "center", fontSize: "2.5rem", marginBottom: 10 }}>
          ✨ Student Task Dashboard
        </h1>
        <p style={{ textAlign: "center", opacity: 0.9, marginBottom: 30 }}>
          “Discipline is the bridge between goals and success.” 🌱
        </p>

        {/* 🔹 Color Legend */}
        <div
          style={{
            display: "flex",
            justifyContent: "space-around",
            marginBottom: "25px",
            padding: "10px 0",
            background: "rgba(255,255,255,0.1)",
            borderRadius: "12px",
          }}
        >
          <span>🔴 &lt;= 3 days</span>
          <span>🟠 ≤ 7 days</span>
          <span>🟡 ≤ 10 days</span>
          <span>🟢 &gt; 10 days</span>
        </div>

        {/* 🔹 Add Task Form */}
        <form
          onSubmit={addTodo}
          style={{
            display: "flex",
            gap: "10px",
            background: "rgba(255,255,255,0.2)",
            borderRadius: "15px",
            padding: "15px",
            marginBottom: "25px",
          }}
        >
          <input
            type="text"
            placeholder="🧠 New task..."
            value={task}
            onChange={(e) => setTask(e.target.value)}
            style={{
              flex: 1,
              border: "none",
              outline: "none",
              borderRadius: "10px",
              padding: "10px 15px",
              fontSize: "1rem",
            }}
          />
          <input
            type="date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
            style={{
              border: "none",
              borderRadius: "10px",
              padding: "10px",
              fontSize: "1rem",
            }}
          />
          <button
            type="submit"
            style={{
              background: "linear-gradient(45deg, #00f5a0, #00d9f5)",
              color: "#222",
              fontWeight: "600",
              border: "none",
              borderRadius: "10px",
              padding: "10px 20px",
              cursor: "pointer",
              transition: "0.2s",
            }}
            onMouseOver={(e) => (e.target.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
          >
            ➕ Add
          </button>
        </form>

        {/* 🔹 Task List */}
        <div style={{ display: "flex", flexDirection: "column", gap: "12px" }}>
          {todos.length === 0 ? (
            <p style={{ textAlign: "center", opacity: 0.8 }}>
              🕓 No tasks yet — start adding your goals!
            </p>
          ) : (
            todos.map((todo) => (
              <TodoItem key={todo._id} todo={todo} refresh={fetchTodos} api={api} />
            ))
          )}
        </div>

        {/* 🔹 Logout */}
        <button
          onClick={logout}
          style={{
            marginTop: "30px",
            width: "100%",
            background: "rgba(255,255,255,0.25)",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            padding: "8px 18px",
            fontSize: "0.95rem",
            letterSpacing: "0.5px",
            cursor: "pointer",
            fontWeight: "600",
            letterSpacing: "0.5px",
            transition: "all 0.2s ease",
            
          }}
        >
          Logout
        </button>
      </div>
    </div>
  );
}
