import React, { useState } from "react";
import axios from "axios";

export default function LoginRegister({ setToken }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLogin, setIsLogin] = useState(true);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = isLogin ? "/users/login" : "/users/register";
    const res = await axios.post(`http://localhost:5000${url}`, {
      username,
      password,
    });

    if (isLogin) {
      setToken(res.data.token);
      localStorage.setItem("token", res.data.token);
    } else {
      alert("Registered successfully! You can now log in.");
      setIsLogin(true);
    }
  };

  return (
    <div className="container" style={{ maxWidth: 400, margin: "80px auto" }}>
      <h2 style={{ textAlign: "center" }}>{isLogin ? "Login" : "Register"}</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          placeholder="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" style={{ width: "100%", marginTop: "10px" }}>
          {isLogin ? "Login" : "Register"}
        </button>
      </form>
      <p style={{ textAlign: "center", marginTop: 10 }}>
        {isLogin ? "No account?" : "Already have an account?"}{" "}
        <span
          style={{ color: "blue", cursor: "pointer" }}
          onClick={() => setIsLogin(!isLogin)}
        >
          {isLogin ? "Register here" : "Login"}
        </span>
      </p>
    </div>
  );
}
