import React, { useState } from "react";

export default function TodoItem({ todo, refresh, api }) {
  const [editing, setEditing] = useState(false);
  const [newText, setNewText] = useState(todo.text);
  const [newDate, setNewDate] = useState(
    todo.dueDate ? todo.dueDate.split("T")[0] : ""
  );

  const toggleComplete = async () => {
    await api.put(`/todos/${todo._id}`, { completed: !todo.completed });
    refresh();
  };

  const deleteTodo = async () => {
    await api.delete(`/todos/${todo._id}`);
    refresh();
  };

  const saveEdit = async () => {
    await api.put(`/todos/${todo._id}`, { text: newText, dueDate: newDate });
    setEditing(false);
    refresh();
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        background: "rgba(255,255,255,0.2)",
        borderRadius: "12px",
        padding: "10px 15px",
        marginBottom: "10px",
        color: "#fff",
        transition: "0.3s",
      }}
    >
      <div style={{ display: "flex", alignItems: "center", gap: "10px", flex: 1 }}>
        <input
          type="checkbox"
          checked={todo.completed}
          onChange={toggleComplete}
          style={{
            transform: "scale(1.3)",
            accentColor: "#43e97b",
            cursor: "pointer",
          }}
        />

        {editing ? (
          <input
            type="text"
            value={newText}
            onChange={(e) => setNewText(e.target.value)}
            style={{
              flex: 1,
              padding: "6px 10px",
              borderRadius: "8px",
              border: "none",
              outline: "none",
              fontSize: "1rem",
              color: "#333",
            }}
          />
        ) : (
          <span
            style={{
              textDecoration: todo.completed ? "line-through" : "none",
              color: todo.completed ? "#bbb" : "#fff",
              fontSize: "1rem",
              flex: 1,
              cursor: "pointer",
            }}
            onDoubleClick={() => setEditing(true)}
          >
            {todo.text}
          </span>
        )}
      </div>

      <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
        {editing ? (
          <>
            <input
              type="date"
              value={newDate}
              onChange={(e) => setNewDate(e.target.value)}
              style={{
                padding: "6px 8px",
                borderRadius: "8px",
                border: "none",
                outline: "none",
                fontSize: "0.9rem",
                color: "#333",
              }}
            />
            <button
              onClick={saveEdit}
              style={{
                background: "#38f9d7",
                border: "none",
                padding: "6px 10px",
                borderRadius: "8px",
                color: "#fff",
                fontWeight: "bold",
                cursor: "pointer",
                transition: "0.2s",
              }}
              onMouseOver={(e) => (e.target.style.background = "#27d3b4")}
              onMouseOut={(e) => (e.target.style.background = "#38f9d7")}
            >
              💾 Save
            </button>
          </>
        ) : (
          <>
            {todo.dueDate && (
              <span
                style={{
                  fontSize: "0.9rem",
                  color: "#e8e8e8",
                  background: "rgba(255,255,255,0.1)",
                  padding: "4px 8px",
                  borderRadius: "8px",
                }}
              >
                📅 {new Date(todo.dueDate).toLocaleDateString()}
              </span>
            )}

            <button
              onClick={() => setEditing(true)}
              style={{
                background: "rgba(255,255,255,0.2)",
                border: "none",
                borderRadius: "8px",
                padding: "6px 8px",
                cursor: "pointer",
                color: "#fff",
                fontWeight: "bold",
                transition: "0.2s",
              }}
              onMouseOver={(e) =>
                (e.target.style.background = "rgba(255,255,255,0.35)")
              }
              onMouseOut={(e) =>
                (e.target.style.background = "rgba(255,255,255,0.2)")
              }
            >
              ✏️
            </button>

            <button
              onClick={deleteTodo}
              style={{
                background: "#ff4b5c",
                border: "none",
                borderRadius: "8px",
                padding: "6px 8px",
                cursor: "pointer",
                color: "#fff",
                fontWeight: "bold",
                transition: "0.2s",
              }}
              onMouseOver={(e) => (e.target.style.background = "#ff1e3c")}
              onMouseOut={(e) => (e.target.style.background = "#ff4b5c")}
            >
              🗑️
            </button>
          </>
        )}
      </div>
    </div>
  );
}
