import React, { useState } from 'react';
import { authFetch } from '../api';


export default function LoginRegister({ setToken }){
const [email,setEmail]=useState('');
const [password,setPassword]=useState('');
const [mode,setMode]=useState('login');
const submit = async (e)=>{
e.preventDefault();
const path = mode==='login'?'/auth/login':'/auth/register';
const res = await authFetch(path, null, { method: 'POST', body: JSON.stringify({ email, password }) });
if (res.token) setToken(res.token); else alert(res.message || 'Error');
};
return (
<div style={{maxWidth:420,margin:'30px auto'}}>
<h2>{mode==='login'?'Login':'Register'}</h2>
<form onSubmit={submit}>
<input value={email} onChange={e=>setEmail(e.target.value)} placeholder="email" required />
<input value={password} onChange={e=>setPassword(e.target.value)} placeholder="password" type="password" required />
<button type="submit">{mode==='login'?'Login':'Register'}</button>
</form>
<button onClick={()=>setMode(mode==='login'?'register':'login')}>Switch to {mode==='login'?'Register':'Login'}</button>
</div>
);
}
