import React, { useEffect, useState } from "react";
import axios from "axios";
import TodoItem from "../components/TodoItem";

export default function Home({ token, setToken }) {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [dueDate, setDueDate] = useState("");

  const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
    headers: { Authorization: `Bearer ${token}` },
  });

  const fetchTodos = async () => {
    try {
      const res = await api.get("/todos");
      setTodos(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (e) => {
    e.preventDefault();
    if (!task.trim()) return;
    try {
      await api.post("/todos", { text: task, dueDate });
      setTask("");
      setDueDate("");
      fetchTodos();
    } catch (err) {
      console.error("Error adding todo:", err);
    }
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem("token");
  };

  return (
    <div className="container" style={{ maxWidth: 700, margin: "40px auto" }}>
      <h1 style={{ textAlign: "center", color: "#333" }}>📝 Your To-Do List</h1>

      {/* 🔹 Color Legend */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          margin: "20px 0",
          padding: "10px",
          background: "#f9f9f9",
          borderRadius: "8px",
          boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
        }}
      >
        <span>🔴 &lt;= 3 days</span>
        <span>🟠 ≤ 7 days</span>
        <span>🟡 ≤ 10 days</span>
        <span>🟢 &gt; 10 days</span>
      </div>

      {/* 🔹 Add Task Form */}
      <form
        onSubmit={addTodo}
        style={{
          display: "flex",
          gap: "10px",
          marginBottom: "20px",
        }}
      >
        <input
          type="text"
          placeholder="Enter new task..."
          value={task}
          onChange={(e) => setTask(e.target.value)}
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            fontSize: "16px",
          }}
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          style={{
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            fontSize: "16px",
          }}
        />
        <button
          type="submit"
          style={{
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "6px",
            padding: "10px 20px",
            cursor: "pointer",
          }}
        >
          Add
        </button>
      </form>

      {/* 🔹 Task List */}
      <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        {todos.map((todo) => (
          <TodoItem key={todo._id} todo={todo} refresh={fetchTodos} api={api} />
        ))}
      </div>

      {/* 🔹 Logout */}
      <button
        onClick={logout}
        style={{
          marginTop: 30,
          background: "crimson",
          color: "white",
          width: "100%",
          border: "none",
          borderRadius: "6px",
          padding: "10px",
          cursor: "pointer",
        }}
      >
        Logout
      </button>
    </div>
  );
}
