import React, { useEffect, useState } from "react";
import axios from "axios";
import TodoItem from "../components/TodoItem";

export default function Home() {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [category, setCategory] = useState("not_important_not_urgent");

  const api = axios.create({
    baseURL: "http://localhost:5000",
  });

  const fetchTodos = async () => {
    const res = await api.get("/todos");
    setTodos(res.data);
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (e) => {
    e.preventDefault();
    if (!task.trim()) return;
    await api.post("/todos", { text: task, dueDate, category });
    setTask("");
    setDueDate("");
    setCategory("not_important_not_urgent");
    fetchTodos();
  };

  return (
    <div className="container" style={{ maxWidth: 600, margin: "50px auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: 20 }}>ðŸ§¾ To-Do List</h2>
      <form onSubmit={addTodo} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <input
          type="text"
          placeholder="Enter your task..."
          value={task}
          onChange={(e) => setTask(e.target.value)}
          required
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="important_urgent">Important & Urgent</option>
          <option value="important_not_urgent">Important & Not Urgent</option>
          <option value="urgentnot_important_">Urgent</option>
          <option value="not_important_not_urgent">Not Important & Not Urgent</option>
        </select>
        <button type="submit" style={{ background: "#4CAF50", color: "white", padding: "8px" }}>
          âž• Add Task
        </button>
      </form>

      <div style={{ marginTop: 20 }}>
        {todos.map((todo) => (
          <TodoItem key={todo._id} todo={todo} refresh={fetchTodos} />
        ))}
      </div>
    </div>
  );
}
