import React from "react";

export default function TodoItem({ todo, refresh, api }) {
  const toggleComplete = async () => {
    await api.put(`/todos/${todo._id}`, { completed: !todo.completed });
    refresh();
  };

  const deleteTodo = async () => {
    await api.delete(`/todos/${todo._id}`);
    refresh();
  };

  return (
    <div
      style={{
        background: todo.completed ? "#d4edda" : "#fff",
        border: "1px solid #ccc",
        borderRadius: "8px",
        padding: "10px",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
      }}
    >
      <div>
        <input type="checkbox" checked={todo.completed} onChange={toggleComplete} />
        <span
          style={{
            marginLeft: "8px",
            textDecoration: todo.completed ? "line-through" : "none",
          }}
        >
          {todo.text}
        </span>
        {todo.dueDate && (
          <span style={{ marginLeft: "10px", color: "gray", fontSize: "0.9em" }}>
            ðŸ“… {new Date(todo.dueDate).toLocaleDateString()}
          </span>
        )}
      </div>
      <button onClick={deleteTodo} style={{ background: "transparent", border: "none", color: "red" }}>
        ðŸ—‘
      </button>
    </div>
  );
}
