import React from "react";

export default function TodoItem({ todo, refresh, api }) {
  const toggleComplete = async () => {
    await api.put(`/todos/${todo._id}`, { completed: !todo.completed });
    refresh();
  };

  const deleteTodo = async () => {
    await api.delete(`/todos/${todo._id}`);
    refresh();
  };

  // ðŸŒˆ Decide color based on date proximity
  const getDueColor = (dueDate) => {
    if (!dueDate) return "#f8f9fa";
    const today = new Date();
    const due = new Date(dueDate);
    const diffDays = Math.ceil((due - today) / (1000 * 60 * 60 * 24));

    if (diffDays <= 3) return "#ffb3b3"; // ðŸ”´ red
    if (diffDays <= 7) return "#ffd699"; // ðŸŸ  orange
    if (diffDays <= 10) return "#ffff99"; // ðŸŸ¡ yellow
    return "#b3ffb3"; // ðŸŸ¢ green
  };

  return (
    <div
      style={{
        background: todo.completed ? "#d4edda" : getDueColor(todo.dueDate),
        border: "1px solid #ccc",
        borderRadius: "8px",
        padding: "10px 15px",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        transition: "background 0.3s ease",
      }}
    >
      <div>
        <input type="checkbox" checked={todo.completed} onChange={toggleComplete} />
        <span
          style={{
            marginLeft: "8px",
            textDecoration: todo.completed ? "line-through" : "none",
            fontWeight: "500",
          }}
        >
          {todo.text}
        </span>
        {todo.dueDate && (
          <span
            style={{
              marginLeft: "10px",
              color: "#555",
              fontSize: "0.9em",
              fontStyle: "italic",
            }}
          >
            ðŸ“… {new Date(todo.dueDate).toLocaleDateString()}
          </span>
        )}
      </div>

      <button
        onClick={deleteTodo}
        style={{
          background: "transparent",
          border: "none",
          color: "red",
          cursor: "pointer",
          fontSize: "1.2em",
        }}
      >
        ðŸ—‘
      </button>
    </div>
  );
}
