import React, { useEffect, useState } from "react";
import axios from "axios";
import TodoItem from "../components/TodoItem";

export default function Home({ token, setToken }) {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [dueDate, setDueDate] = useState("");

  const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
    headers: { Authorization: `Bearer ${token}` },
  });

  const fetchTodos = async () => {
    try {
      const res = await api.get("/todos");
      // ‚úÖ Sort by date: earliest first
      const sorted = res.data.sort((a, b) => {
        if (!a.dueDate) return 1;
        if (!b.dueDate) return -1;
        return new Date(a.dueDate) - new Date(b.dueDate);
      });
      setTodos(sorted);
    } catch (err) {
      console.error("Error fetching todos:", err);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (e) => {
    e.preventDefault();
    if (!task.trim()) return;
    try {
      await api.post("/todos", { text: task, dueDate });
      setTask("");
      setDueDate("");
      fetchTodos();
    } catch (err) {
      console.error("Error adding todo:", err);
    }
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem("token");
  };

  const getDateColor = (dueDate) => {
    if (!dueDate) return "#ccc";
    const today = new Date();
    const date = new Date(dueDate);
    today.setHours(0, 0, 0, 0);
    date.setHours(0, 0, 0, 0);

    if (date < today) return "#ff4b5c"; // üî¥ overdue
    if (date.getTime() === today.getTime()) return "#ffcc00"; // üü° today
    return "#43e97b"; // üü¢ upcoming
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "50px 20px",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "850px",
          background: "rgba(255,255,255,0.15)",
          borderRadius: "20px",
          boxShadow: "0 8px 32px rgba(31,38,135,0.37)",
          backdropFilter: "blur(8.5px)",
          border: "1px solid rgba(255,255,255,0.18)",
          padding: "30px",
          color: "#fff",
          position: "relative",
        }}
      >
        {/* Header */}
        <h1
          style={{
            textAlign: "center",
            marginBottom: "10px",
            fontSize: "2.2rem",
            fontWeight: "700",
          }}
        >
          üóìÔ∏è Smart To-Do Planner
        </h1>
        <p
          style={{
            textAlign: "center",
            marginBottom: "25px",
            fontSize: "1rem",
            color: "#f0f0f0",
          }}
        >
          Organize your tasks by date and priority
        </p>

        {/* Logout */}
        <button
          onClick={logout}
          style={{
            position: "absolute",
            top: "20px",
            right: "20px",
            background: "#ff4b5c",
            color: "#fff",
            border: "none",
            borderRadius: "20px",
            padding: "8px 14px",
            cursor: "pointer",
            fontWeight: "600",
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.background = "#ff1e3c")}
          onMouseOut={(e) => (e.target.style.background = "#ff4b5c")}
        >
          Logout
        </button>

        {/* Mini Calendar Overview */}
        <div
          style={{
            marginBottom: "25px",
            padding: "10px 15px",
            borderRadius: "12px",
            background: "rgba(255,255,255,0.1)",
            display: "flex",
            justifyContent: "space-around",
            color: "#fff",
            fontSize: "0.95rem",
          }}
        >
          <div>üìÖ Today: {new Date().toLocaleDateString()}</div>
          <div>
            ‚è∞ Total Tasks: <strong>{todos.length}</strong>
          </div>
          <div>
            ‚úÖ Completed:{" "}
            <strong>{todos.filter((t) => t.completed).length}</strong>
          </div>
        </div>

        {/* Add Task Form */}
        <form
          onSubmit={addTodo}
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "center",
            alignItems: "center",
            gap: "10px",
            marginBottom: "25px",
          }}
        >
          <input
            type="text"
            placeholder="üìù Add a new task..."
            value={task}
            onChange={(e) => setTask(e.target.value)}
            style={{
              flex: "1 1 55%",
              padding: "12px 18px",
              borderRadius: "30px",
              border: "none",
              outline: "none",
              background: "rgba(255,255,255,0.9)",
              fontSize: "1rem",
              color: "#333",
            }}
          />
          <input
            type="date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
            style={{
              flex: "1 1 25%",
              padding: "12px 15px",
              borderRadius: "30px",
              border: "none",
              outline: "none",
              background: "rgba(255,255,255,0.9)",
              fontSize: "1rem",
              color: "#333",
            }}
          />
          <button
            type="submit"
            style={{
              background: "linear-gradient(45deg, #43e97b, #38f9d7)",
              border: "none",
              borderRadius: "30px",
              padding: "12px 25px",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "1rem",
              color: "#fff",
              transition: "transform 0.2s ease",
            }}
            onMouseOver={(e) => (e.target.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
          >
            ‚ûï Add
          </button>
        </form>

        {/* Todo List */}
        <div
          style={{
            maxHeight: "400px",
            overflowY: "auto",
            padding: "10px",
            borderRadius: "10px",
          }}
        >
          {todos.length === 0 ? (
            <p
              style={{
                textAlign: "center",
                color: "#eee",
                fontStyle: "italic",
              }}
            >
              No tasks yet. Add your first one! üöÄ
            </p>
          ) : (
            todos.map((todo) => {
              const color = getDateColor(todo.dueDate);
              return (
                <div
                  key={todo._id}
                  style={{
                    marginBottom: "12px",
                    background: "rgba(255,255,255,0.25)",
                    borderRadius: "12px",
                    padding: "12px 20px",
                    boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
                    borderLeft: `6px solid ${color}`,
                  }}
                >
                  <TodoItem todo={todo} refresh={fetchTodos} api={api} />
                  {todo.dueDate && (
                    <p
                      style={{
                        marginTop: "5px",
                        fontSize: "0.9rem",
                        color,
                        fontWeight: "600",
                      }}
                    >
                      üìÖ {new Date(todo.dueDate).toLocaleDateString()}
                    </p>
                  )}
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}
