import React, { useEffect, useState } from "react";
import axios from "axios";
import TodoItem from "../components/TodoItem";

export default function Home({ token, setToken }) {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [dueDate, setDueDate] = useState("");

  const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL,
    headers: { Authorization: `Bearer ${token}` },
  });

  const fetchTodos = async () => {
    try {
      const res = await api.get("/todos");
      setTodos(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (e) => {
    e.preventDefault();
    if (!task.trim()) return;
    try {
      await api.post("/todos", { text: task, dueDate });
      setTask("");
      setDueDate("");
      fetchTodos();
    } catch (err) {
      console.error("Error adding todo:", err);
    }
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem("token");
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)",
        color: "#fff",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "40px 15px",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <h1 style={{ fontSize: "2.5rem", marginBottom: "10px", fontWeight: "700" }}>
        ðŸŽ¯ Student Task Planner
      </h1>
      <p style={{ fontSize: "1rem", opacity: 0.9, marginBottom: "30px" }}>
        Stay organized, stay ahead! Plan smart with deadlines & colors ðŸš€
      </p>

      {/* ðŸ”¹ Color Legend */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "20px",
          background: "rgba(255,255,255,0.15)",
          padding: "10px 20px",
          borderRadius: "12px",
          marginBottom: "30px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
        }}
      >
        <span>ðŸ”´ &lt;= 3 days</span>
        <span>ðŸŸ  â‰¤ 7 days</span>
        <span>ðŸŸ¡ â‰¤ 10 days</span>
        <span>ðŸŸ¢ &gt; 10 days</span>
      </div>

      {/* ðŸ”¹ Add Task Form */}
      <form
        onSubmit={addTodo}
        style={{
          display: "flex",
          gap: "10px",
          background: "rgba(255,255,255,0.15)",
          padding: "20px",
          borderRadius: "15px",
          width: "100%",
          maxWidth: "600px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
          backdropFilter: "blur(10px)",
        }}
      >
        <input
          type="text"
          placeholder="ðŸŽ“ Enter your next study task..."
          value={task}
          onChange={(e) => setTask(e.target.value)}
          style={{
            flex: 1,
            padding: "10px 15px",
            borderRadius: "10px",
            border: "none",
            outline: "none",
            fontSize: "1rem",
          }}
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          style={{
            padding: "10px",
            borderRadius: "10px",
            border: "none",
            fontSize: "1rem",
          }}
        />
        <button
          type="submit"
          style={{
            background: "#00ffb3",
            color: "#222",
            fontWeight: "700",
            border: "none",
            borderRadius: "10px",
            padding: "10px 20px",
            cursor: "pointer",
            transition: "all 0.2s ease",
          }}
          onMouseOver={(e) => (e.target.style.transform = "scale(1.05)")}
          onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
        >
          âž• Add
        </button>
      </form>

      {/* ðŸ”¹ Task List */}
      <div
        style={{
          width: "100%",
          maxWidth: "600px",
          marginTop: "25px",
          display: "flex",
          flexDirection: "column",
          gap: "12px",
        }}
      >
        {todos.length === 0 ? (
          <p style={{ textAlign: "center", opacity: 0.8, marginTop: "30px" }}>
            âœ¨ No tasks yet â€” add your first goal!
          </p>
        ) : (
          todos.map((todo) => (
            <TodoItem key={todo._id} todo={todo} refresh={fetchTodos} api={api} />
          ))
        )}
      </div>

      {/* ðŸ”¹ Logout */}
      <button
        onClick={logout}
        style={{
          marginTop: "40px",
          background: "rgba(255,255,255,0.2)",
          color: "white",
          border: "none",
          borderRadius: "8px",
          padding: "10px 25px",
          cursor: "pointer",
          fontWeight: "600",
          transition: "0.3s",
        }}
        onMouseOver={(e) => (e.target.style.background = "rgba(255,255,255,0.3)")}
        onMouseOut={(e) => (e.target.style.background = "rgba(255,255,255,0.2)")}
      >
        ðŸšª Logout
      </button>
    </div>
  );
}
