import React from "react";

export default function TodoItem({ todo, refresh, api }) {
  const toggleComplete = async () => {
    await api.put(`/todos/${todo._id}`, { completed: !todo.completed });
    refresh();
  };

  const deleteTodo = async () => {
    await api.delete(`/todos/${todo._id}`);
    refresh();
  };

  return (
    <div style={{
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      background: "#f4f4f4",
      margin: "8px 0",
      padding: "10px",
      borderRadius: "10px",
    }}>
      <div>
        <input type="checkbox" checked={todo.completed} onChange={toggleComplete} />
        <span style={{
          textDecoration: todo.completed ? "line-through" : "none",
          marginLeft: "10px",
        }}>
          {todo.text} â€” {todo.dueDate ? new Date(todo.dueDate).toLocaleDateString() : "No date"}
        </span>
      </div>
      <button onClick={deleteTodo}>ðŸ—‘</button>
    </div>
  );
}
