import React from "react";
import axios from "axios";

export default function TodoItem({ todo, refresh }) {
  const api = axios.create({ baseURL: "http://localhost:5000" });

  const toggleComplete = async () => {
    await api.put(`/todos/${todo._id}`, { completed: !todo.completed });
    refresh();
  };

  const deleteTodo = async () => {
    await api.delete(`/todos/${todo._id}`);
    refresh();
  };

  return (
    <div
      style={{
        background: "#fff",
        padding: "10px",
        marginBottom: "10px",
        borderRadius: "8px",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
      }}
    >
      <div>
        <input
          type="checkbox"
          checked={todo.completed}
          onChange={toggleComplete}
          style={{ marginRight: "10px" }}
        />
        <strong style={{ textDecoration: todo.completed ? "line-through" : "none" }}>
          {todo.text}
        </strong>
        {todo.dueDate && (
          <small style={{ marginLeft: 10, color: "gray" }}>
            (Due: {new Date(todo.dueDate).toLocaleDateString()})
          </small>
        )}
      </div>
      <button
        onClick={deleteTodo}
        style={{
          background: "crimson",
          color: "white",
          border: "none",
          borderRadius: "5px",
          padding: "5px 8px",
        }}
      >
        ‚ùå
      </button>
    </div>
  );
}
