import React, { useEffect, useState } from "react";
import axios from "axios";
import TodoItem from "../components/TodoItem";

export default function Home({ token, setToken }) {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [category, setCategory] = useState("not_important_not_urgent");

  const api = axios.create({
    baseURL: "http://localhost:5000",
    headers: { Authorization: `Bearer ${token}` },
  });

  const fetchTodos = async () => {
    const res = await api.get("/todos");
    setTodos(res.data);
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (e) => {
    e.preventDefault();
    await api.post("/todos", { text: task, category, dueDate });
    setTask("");
    setDueDate("");
    fetchTodos();
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem("token");
  };

  return (
    <div className="container" style={{ maxWidth: 600, margin: "50px auto" }}>
      <h2 style={{ textAlign: "center" }}>ðŸ§¾ Your Tasks</h2>
      <form onSubmit={addTodo}>
        <input
          placeholder="Enter task..."
          value={task}
          onChange={(e) => setTask(e.target.value)}
          required
        />
        <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="important_urgent">Important & Urgent</option>
          <option value="important_not_urgent">Important & Not Urgent</option>
          <option value="not_important_urgent">Not Important & Urgent</option>
          <option value="not_important_not_urgent">Not Important & Not Urgent</option>
        </select>
        <button type="submit">Add Task</button>
      </form>

      <div style={{ marginTop: 20 }}>
        {todos.map((todo) => (
          <TodoItem key={todo._id} todo={todo} api={api} refresh={fetchTodos} />
        ))}
      </div>

      <button onClick={logout} style={{ marginTop: 20, background: "crimson", color: "white" }}>
        Logout
      </button>
    </div>
  );
}
