import React from 'react';

export default function TodoItem({ todo, onToggle, onDelete }) {
return (
<div
style={{
display: 'flex',
alignItems: 'center',
justifyContent: 'space-between',
padding: '10px 15px',
borderBottom: '1px solid #ddd',
backgroundColor: todo.done ? '#f5f5f5' : '#fff',
borderRadius: '8px',
marginBottom: '8px',
}}
>
<div style={{ display: 'flex', alignItems: 'center', gap: '10px', flex: 1 }}>
<input
type="checkbox"
checked={todo.done}
onChange={onToggle}
style={{ transform: 'scale(1.3)', cursor: 'pointer' }}
/>
<span
style={{
flex: 1,
textDecoration: todo.done ? 'line-through' : 'none',
color: todo.done ? '#999' : '#222',
fontSize: '16px',
}}
>
{todo.text} </span> </div>
<button
onClick={onDelete}
style={{
backgroundColor: '#ff4d4d',
color: 'white',
border: 'none',
padding: '6px 10px',
borderRadius: '5px',
cursor: 'pointer',
}}
>
Delete </button> </div>
);
}
